import paletteBlue from "../images/Blue.jpg";
import paletteRed from "../images/red.jpg";
import paletteMixed from "../images/mixed.jpg";
import Palette from "./Palette";

class Design extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isOpen: false,
    };
    this.handleHeaderClick = this.handleHeaderClick.bind(this);
  }
  handleHeaderClick() {
    this.setState({
      isHidden: !this.state.isHidden,
    });
  }

  render() {
    const openClassName = this.state.isHidden ? "js-hidden" : "";
    return (
      // <fieldset className='user-options'>
      <div className={`color-design ${openClassName}`}>
        <div className="color-container">
          <p>COLORES</p>
        </div>
        <div className="radio-color">
          <Palette
            className={"js-form js-palette color js-color1"}
            id={"color1"}
            value={"blue"}
            checked
            src={paletteBlue}
          ></Palette>
          <Palette
            className={"js-form js-palette color js-color2"}
            id={"color2"}
            value={"red"}
            src={paletteRed}
          ></Palette>
          <Palette
            className={"js-form js-palette color js-color3"}
            id={"color3"}
            value={"mixedcolor"}
            src={paletteMixed}
          ></Palette>
        </div>
      </div>

      // </fieldset>
    );
  }
}

export default Design;
