import React from "react";
import "../stylesheets/App.scss";

class Collapsible extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isOpen: false,
    };
    this.handleHeaderClick = this.handleHeaderClick.bind(this);
  }
  handleHeaderClick() {
    this.setState({
      isHidden: !this.state.isHidden,
    });
  }
  render() {
    const openClassName = this.state.isHidden ? "js-hidden" : "";

    return (
      <>
        <div className={`icon2 js-design-title`}>
          <i className="far fa-object-group list-icon2b"></i>
          <h2 className="legend-fill" onClick={this.handleHeaderClick}>
            {this.props.className}
          </h2>

          <button className={`ico-scroll ${this.props.classButtonUp}`}>
            <i className="fas fa-angle-up"></i>
          </button>

          <button
            className={`ico-scroll ${this.props.classButtonDown} js-hidden`}
          >
            <i className="fas fa-angle-down"></i>
          </button>
        </div>
        {/* //<div>{this.props.children}</div> */}
      </>
    );
  }
}

export default Collapsible;
