import React, { useState } from "react";
import CardPreview from "./CardPreview";
import Form from "./Form";

function Main() {
  const [name, setName] = useState("");
  const [job, setJob] = useState("");
  const [data, setData] = useState({ name: "", job: "", email: "" });
  const [avatar, setAvatar] = useState("");
  const handleChangeInputs = (inputValue, inputName) => {
    console.log("Change!", inputValue, inputName);
    //setName(nuevoValor);
    setData({
      ...data,
      [inputName]: inputValue,
    });
  };
  return (
    <section className="main_container">
      <CardPreview name={name} avatar={avatar} />
      <Form handleChangeInputs={handleChangeInputs} />{" "}
    </section>
  );
}
export default Main;
